
public class 반복문_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("===== for문 =====");
		for(int i=1;i<=10;i++) {
			System.out.print(i + " ");
		}
		
		System.out.println();
		System.out.println("===== while문 =====");
		int i=1;
		while(i<=10) {
			System.out.print(i + " ");
			i++;
		}
		System.out.println();
	}

}
